mapper.select:
SELECT 

FROM my_message a

WHERE a.id = #{id}

SELECT 

FROM my_message a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.user_id = #{user.id}


		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM my_message a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE my_message SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
