mapper.select:
SELECT 

FROM my_behaviour a

WHERE a.id = #{id}

SELECT
FROM my_behaviour a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.stu_id LIKE '%'||#{student}||'%' '%'+#{student}+'%' concat('%',#{student},'%')
AND a.beha_date = #{behaDate}
AND a.tea_id LIKE '%'||#{teacher}||'%' '%'+#{teacher}+'%' concat('%',#{teacher},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM my_behaviour a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE my_behaviour SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
