mapper.select:
SELECT 

FROM my_student a

WHERE a.id = #{id}

SELECT 

FROM my_student a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.sex = #{sex}


		AND a.trusts_id = #{trusts.id}


		AND a.birthday = #{birthday}


		AND a.school = #{school}


		AND a.classes_id = #{classesId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM my_student a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE my_student SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
